-- Table: account
CREATE TABLE account (
    id integer(32,0) DEFAULT nextval('account_id_seq'::regclass) NOT NULL,
    name text,
    email text,
    email_verified boolean,
    created_at timestamp with time zone,
    pw text,
    PRIMARY KEY (id),
    UNIQUE (email)
);

CREATE UNIQUE INDEX account_email_key ON public.account USING btree (email);
CREATE INDEX accountemailindex ON public.account USING btree (email);
-- Table: bills
CREATE TABLE bills (
    order_id character varying(20) NOT NULL,
    room_number character varying(10) NOT NULL,
    guest_name character varying(50),
    deposit numeric(10,2),
    refund_deposit boolean NOT NULL,
    room_fee numeric(10,2),
    total_income numeric(10,2),
    pay_way character varying(20) NOT NULL,
    create_time timestamp without time zone NOT NULL,
    remarks text,
    review_invited boolean DEFAULT false,
    positive_review boolean,
    review_invite_time timestamp without time zone,
    review_update_time timestamp without time zone,
    PRIMARY KEY (order_id),
    FOREIGN KEY (order_id) REFERENCES public.orders(order_id)
);

CREATE INDEX idx_bills_order_id ON public.bills USING btree (order_id);
CREATE INDEX idx_bills_review_invited ON public.bills USING btree (review_invited);
CREATE INDEX idx_bills_positive_review ON public.bills USING btree (positive_review);
CREATE INDEX idx_bills_review_invite_time ON public.bills USING btree (review_invite_time);
-- Table: inventory
CREATE TABLE inventory (
    date date NOT NULL,
    type_code character varying(20) NOT NULL,
    total_rooms integer NOT NULL,
    available_rooms integer NOT NULL,
    price_adjustment numeric(10,2),
    PRIMARY KEY (date, type_code),
    FOREIGN KEY (type_code) REFERENCES public.room_types(type_code)
);

CREATE INDEX idx_inventory_date ON public.inventory USING btree (date);
-- Table: orders
CREATE TABLE orders (
    order_id character varying(20) NOT NULL,
    id_source character varying(50),
    order_source character varying(20) NOT NULL,
    guest_name character varying(50) NOT NULL,
    phone character varying(20) NOT NULL,
    id_number character varying(30) NOT NULL,
    room_type character varying(20) NOT NULL,
    room_number character varying(20) NOT NULL,
    check_in_date date NOT NULL,
    check_out_date date NOT NULL,
    status character varying(20) NOT NULL,
    payment_method character varying(20),
    room_price numeric(10,2) NOT NULL,
    deposit numeric(10,2),
    create_time timestamp without time zone NOT NULL,
    remarks text,
    refunded_deposit numeric(10,2) DEFAULT 0,
    refund_records jsonb DEFAULT '[]'::jsonb,
    PRIMARY KEY (order_id),
    FOREIGN KEY (room_type) REFERENCES public.room_types(type_code),
    FOREIGN KEY (room_number) REFERENCES public.rooms(room_number)
);

CREATE INDEX idx_orders_status ON public.orders USING btree (status);
CREATE INDEX idx_orders_check_dates ON public.orders USING btree (check_in_date, check_out_date);
-- Table: room_types
CREATE TABLE room_types (
    type_code character varying(20) NOT NULL,
    type_name character varying(50) NOT NULL,
    base_price numeric(10,2) NOT NULL,
    description text,
    is_closed boolean DEFAULT false NOT NULL,
    PRIMARY KEY (type_code)
);

CREATE INDEX idx_room_types_name ON public.room_types USING btree (type_name);
-- Table: rooms
CREATE TABLE rooms (
    room_id integer NOT NULL,
    room_number character varying(20) NOT NULL,
    type_code character varying(20) NOT NULL,
    status character varying(20) NOT NULL,
    price numeric(10,2) NOT NULL,
    is_closed boolean DEFAULT false,
    PRIMARY KEY (room_id),
    FOREIGN KEY (type_code) REFERENCES public.room_types(type_code),
    UNIQUE (room_number)
);

CREATE INDEX idx_rooms_status ON public.rooms USING btree (status);
CREATE INDEX idx_rooms_type ON public.rooms USING btree (type_code);
CREATE UNIQUE INDEX rooms_room_number_key ON public.rooms USING btree (room_number);
CREATE INDEX idx_rooms_number ON public.rooms USING btree (room_number);
-- Table: shift_handover
CREATE TABLE shift_handover (
    id integer(32,0) DEFAULT nextval('shift_handover_id_seq'::regclass) NOT NULL,
    type character varying(20) DEFAULT 'hotel'::character varying NOT NULL,
    details jsonb DEFAULT '[]'::jsonb NOT NULL,
    statistics jsonb DEFAULT '{}'::jsonb NOT NULL,
    remarks text,
    cashier_name character varying(100) NOT NULL,
    shift_time character varying(10) NOT NULL,
    shift_date date NOT NULL,
    created_at timestamp with time zone DEFAULT CURRENT_TIMESTAMP,
    updated_at timestamp with time zone DEFAULT CURRENT_TIMESTAMP,
    html_snapshot text,
    handover_person character varying(100),
    receive_person character varying(100),
    PRIMARY KEY (id)
);

CREATE INDEX idx_shift_handover_date ON public.shift_handover USING btree (shift_date);
CREATE INDEX idx_shift_handover_type ON public.shift_handover USING btree (type);
